<?php/*** some utiliities**/class SforumUtils {	public static function time() {		return time();	}		public static function displayDateTime($timestamp) {		return date('F j, Y, g:i a', $timestamp);	}		public static function post( $text ) {		$text = nl2br(CHtml::encode($text));				$text = preg_replace("/\[(\/?((b)|(p)|(li)|(ul)|(h1)|(h2)|(h3)|(h4)|(h5)|(h6)))\]/iU", "<$1>", $text);				return $text;	}		public static function showFlash() {		if( Yii::app()->user->hasFlash('success') ) {			$flash = Yii::app()->user->getFlash('success');			return '<div id="success-flash" class="flash-success">			' . $flash . '			</div><script>fadeAway(\'success-flash\');</script>';		}				return null;	}		public static function getMailer() {		$path = Yii::getPathOfAlias('application.extensions.mailer.EMailer') . '.php';				if( file_exists($path) ) {			$mailer = Yii::createComponent('application.extensions.mailer.EMailer');						//$mailer->Host = <your smtp host>;			//$mailer->IsSMTP();						$adminEmail = isset(Yii::app()->params['adminEmail'])?Yii::app()->params['adminEmail']:null;						if( $adminEmail ) {				$mailer->From = $adminEmail;				$mailer->AddReplyTo($adminEmail);				$mailer->AddBCC($adminEmail);			}			$mailer->FromName = Yii::app()->name;						$mailer->CharSet = 'UTF-8';			//$mailer->Subject = Yii::t('demo', 'Yii rulez!');			//$mailer->Body = $message;			//$mailer->Send();						return $mailer;		}				return null;	}}