<?php/*** some utiliities**/class SforumUtils {	public static function time() {		return time();	}		public static function displayDateTime($timestamp) {		return date('F j, Y, g:i a', $timestamp);	}		public static function post( $text ) {		$text = nl2br(CHtml::encode($text));				$text = preg_replace("/\[(\/?((b)|(p)|(li)|(ul)|(h1)|(h2)|(h3)|(h4)|(h5)|(h6)))\]/iU", "<$1>", $text);				return $text;	}}